        'saws' => 'Saws',
        'multi-tools' => 'Multi-Tools',
        'grinders' => 'Grinders',
        'sanders' => 'Sanders',
    ];

    foreach ($categories as $slug => $name) {
        if (!term_exists($slug, 'category')) {
            wp_insert_term($name, 'category', ['slug' => $slug]);
        }
    }

    $posts = [
